-- Дамп структуры для таблица db_news.news
CREATE TABLE IF NOT EXISTS `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `text` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `url_pic` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `date` datetime DEFAULT NULL,
  `heading` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_news_heading` (`heading`),
  CONSTRAINT `FK_news_heading` FOREIGN KEY (`heading`) REFERENCES `heading` (`id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=59 DEFAULT CHARSET=utf8;

